import puppeteer from "@cloudflare/puppeteer";

// inlining this because Wrangler can't use asset and browser bindings simultaneously
let font =
  "data:font/ttf;base64,";

let style = `
  @font-face {
      font-family: "TomThumb";
      src: url("${font}") format("truetype");
  }

  #day {
      display: flex;
      justify-content: center;
      align-items: center;
      text-transform: uppercase;
      background-color: #fff;
      color: #000;
      height: 7px;
  }

  #name {
      display: flex;
      justify-content: center;
      align-items: center;
      height: 24px;
  }

  html,
  body {
      background-color: #000;
      color: #fff;
      text-align: center;
      width: 64px;
      height: 32px;
      margin: 0;
      padding: 0;
      font-family: "TomThumb";
      font-size: 6px;
      text-align: center;
      font-smooth: never;
      -webkit-font-smoothing: none;
  }
`;

export default {
  async scheduled(_, env) {
    try {
      let toDateString = (date) =>
        date
          .toLocaleString("en-US", {
            timeZone: "America/New_York",
            day: "2-digit",
            month: "2-digit",
            year: "numeric",
          })
          .replace(/\//g, "-");

      let start = new Date();
      let end = new Date(start);
      end.setDate(end.getDate() + 7);

      let endpoint = `https://api.mealviewer.com/api/v4/school/${env.SCHOOL_ID}`;
      let url = `${endpoint}/${toDateString(start)}/${toDateString(end)}/0`;
      let res = await fetch(url, { headers: { Accept: "application/json" } });
      let { menuSchedules } = await res.json();

      for (let [i, schedule] of Object.entries(menuSchedules)) {
        if (i === "0") {
          let hour = +new Date().toLocaleString("en-US", {
            timeZone: env.TZ,
            hour: "numeric",
            hour12: false,
          });

          if (hour > 12) continue;
        }

        let {
          dateInformation: { weekDayName },
          menuBlocks,
        } = schedule;
        let lunch = menuBlocks.find((b) => b.blockName === "Lunch");
        if (!lunch) continue;

        let { item_Name, item_Type } =
          lunch.cafeteriaLineList.data[0].foodItemList.data[0];
        let name = item_Type === "Entrée" ? item_Name : item_Type;
        let day = i === "0" ? "today" : i === "1" ? "tomorrow" : weekDayName;

        let head = `<head><style>${style}</style></head>`;
        let body = `<body><div id="day">lunch ${day}</div><div id="name">${name}</div></body>`;
        let html = `<!DOCTYPE html><html>${head}${body}</html>`;

        let browser = await puppeteer.launch(env.BROWSER);
        let page = await browser.newPage();
        await page.setViewport({ width: 64, height: 32 });
        await page.setContent(html);
        let screenshot = await page.screenshot({ type: "webp" });
        let b64 = btoa(String.fromCharCode(...new Uint8Array(screenshot)));
        await browser.close();

        let url = `https://api.tidbyt.com/v0/devices/${env.DEVICE_ID}/push`;
        let response = await fetch(url, {
          method: "POST",
          headers: {
            Authorization: `Bearer ${env.API_KEY}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            image: b64,
            installationID: "MealViewer",
            background: false,
          }),
        });

        if (!response.ok) throw new Error(await response.text());

        return new Response(screenshot, {
          headers: {
            "Content-Type": "image/webp",
          },
        });
      }
    } catch (e) {
      return new Response(e.message);
    }
  },
};
